DROP TABLE IF EXISTS `药品`;
CREATE TABLE `药品`  (
  `药品ID` int(11) NOT NULL,
  `药品名称` varchar(45) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `成分性状` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `适用病症` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `用法用量` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `不良反应` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '无',
  `禁忌` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '无',
  `注意事项` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '无',
  `药物过量` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '无',
